    const CompanyName = "TechCorp"

    // Arrays
    var numbers [5]int = [5]int{1, 2, 3, 4, 5}
    fruits := [3]string{"apple", "banana", "orange"}

    // Slices (dynamic arrays)
    colors := []string{"red", "green", "blue"}
    colors = append(colors, "yellow")

    // Maps
    person := map[string]interface{}{
        "name":      "Bob",
        "age":       25,
        "isStudent": false,
    }

