fn main() {
    // Basic output
    println!("Hello, Rust!");

    // Variables and mutability
    let name = "Alice";  // Immutable by default
    let mut age = 30;    // Mutable variable
    age = 31;            // Can change mutable variables

    // Data types
    let integer: i32 = 42;
    let float: f64 = 3.14;
    let boolean: bool = true;
    let character: char = 'R';

    // String types
    let string_literal = "Hello";        // &str (string slice)
    let owned_string = String::from("World");  // String (owned)
    let combined = format!("{} {}", string_literal, owned_string);

    // Arrays and vectors
    let array: [i32; 5] = [1, 2, 3, 4, 5];
    let mut vector = vec![1, 2, 3];
    vector.push(4);

    // Tuples
    let coordinates: (f64, f64) = (3.0, 4.0);
    let (x, y) = coordinates;  // Destructuring

    // Ownership example
    let s1 = String::from("hello");
    let s2 = s1;  // s1 is moved to s2, s1 is no longer valid
    // println!("{}", s1);  // This would cause a compile error
    println!("{}", s2);  // This works

    // Borrowing (references)
    let s3 = String::from("world");
    let len = calculate_length(&s3);  // Borrow s3
    println!("Length of '{}' is {}", s3, len);  // s3 is still valid

    // Mutable references
    let mut s4 = String::from("hello");
    change_string(&mut s4);
    println!("Changed string: {}", s4);

    // Slices
    let word = String::from("hello world");
    let hello = &word[0..5];
    let world = &word[6..11];
    println!("First word: {}, Second word: {}", hello, world);

    // Control flow
    if age > 18 {
        println!("Adult");
    } else {
        println!("Minor");
    }

    // Pattern matching with match
    let number = 3;
    match number {
        1 => println!("One"),
        2 => println!("Two"),
        3 => println!("Three"),
        _ => println!("Other"),
    }

    // Loops
    for i in 0..5 {
        println!("Number: {}", i);
    }

    for item in &vector {
        println!("Vector item: {}", item);
    }

    let mut counter = 0;
    while counter < 3 {
        println!("Counter: {}", counter);
        counter += 1;
    }

    // Function calls
    let result = add_numbers(5, 3);
    println!("5 + 3 = {}", result);

    let (sum, product) = calculate_sum_and_product(4, 6);
    println!("Sum: {}, Product: {}", sum, product);
}

// Function that borrows a string
fn calculate_length(s: &String) -> usize {
    s.len()
}

// Function that borrows a mutable string
fn change_string(s: &mut String) {
    s.push_str(", world!");
}

// Function with parameters and return type
fn add_numbers(a: i32, b: i32) -> i32 {
    a + b  // No semicolon = return value
}

// Function returning multiple values
fn calculate_sum_and_product(a: i32, b: i32) -> (i32, i32) {
    (a + b, a * b)
}

