// Node.js EC2 Management
const { ec2 } = require('./aws-config');

// Launch EC2 instance
async function launchInstance() {
  const params = {
    ImageId: 'ami-0abcdef1234567890',
    MinCount: 1,
    MaxCount: 1,
    InstanceType: 't2.micro',
    KeyName: 'my-key-pair',
    SecurityGroupIds: ['sg-1234567890abcdef0'],
    TagSpecifications: [
      {
        ResourceType: 'instance',
        Tags: [
          {
            Key: 'Name',
            Value: 'MyWebServer'
          },
          {
            Key: 'Environment',
            Value: 'Development'
          }
        ]
      }
    ],
    UserData: Buffer.from(`#!/bin/bash
      yum update -y
      yum install -y httpd nodejs npm
      systemctl start httpd
      systemctl enable httpd

      # Create a simple Node.js app
      cat > /home/ec2-user/app.js << 'EOF'
      const http = require('http');
      const server = http.createServer((req, res) => {
        res.writeHead(200, {'Content-Type': 'text/html'});
        res.end('<h1>Hello from Node.js on AWS EC2!</h1>');
      });
      server.listen(3000, () => {
        console.log('Server running on port 3000');
      });
      EOF

      # Start Node.js app
      cd /home/ec2-user
      nohup node app.js > app.log 2>&1 &
    `).toString('base64')
  };

  try {
    const result = await ec2.runInstances(params).promise();
    console.log('Instance launched:', result.Instances[0].InstanceId);
    return result.Instances[0];
  } catch (error) {
    console.error('Error launching instance:', error);
    throw error;
  }
}

// Terminate instance
async function terminateInstance(instanceId) {
  const params = {
    InstanceIds: [instanceId]
  };

  try {
    const result = await ec2.terminateInstances(params).promise();
    console.log('Instance terminated:', result.TerminatingInstances);
    return result.TerminatingInstances;
  } catch (error) {
    console.error('Error terminating instance:', error);
    throw error;
  }
}

// Get instance status
async function getInstanceStatus(instanceId) {
  const params = {
    InstanceIds: [instanceId]
  };

  try {
    const result = await ec2.describeInstanceStatus(params).promise();
    return result.InstanceStatuses[0];
  } catch (error) {
    console.error('Error getting instance status:', error);
    throw error;
  }
}

// Create and attach EBS volume
async function createAndAttachVolume(instanceId, availabilityZone) {
  // Create volume
  const createParams = {
    Size: 10,  // 10 GB
    VolumeType: 'gp3',
    AvailabilityZone: availabilityZone,
    TagSpecifications: [
      {
        ResourceType: 'volume',
        Tags: [
          {
            Key: 'Name',
            Value: 'MyDataVolume'
          }
        ]
      }
    ]
  };

  try {
    const createResult = await ec2.createVolume(createParams).promise();
    const volumeId = createResult.VolumeId;
    console.log('Volume created:', volumeId);

    // Wait for volume to be available
    await ec2.waitFor('volumeAvailable', { VolumeIds: [volumeId] }).promise();

    // Attach volume
    const attachParams = {
      VolumeId: volumeId,
      InstanceId: instanceId,
      Device: '/dev/sdf'
    };

    const attachResult = await ec2.attachVolume(attachParams).promise();
    console.log('Volume attached:', attachResult);
    return { volumeId, attachResult };
  } catch (error) {
    console.error('Error creating/attaching volume:', error);
    throw error;
  }
}

