// Create droplet
async function createDroplet() {
  const dropletConfig = {
    name: 'my-web-server',
    region: 'nyc3',
    size: 's-1vcpu-1gb',
    image: 'ubuntu-20-04-x64',
    ssh_keys: [], // Add your SSH key IDs
    backups: false,
    ipv6: true,
    user_data: `#!/bin/bash
      apt update
      apt install -y nginx nodejs npm
      systemctl start nginx
      systemctl enable nginx

      # Create simple Node.js app
      cat > /home/app.js << 'EOF'
