// User operations
const { connectDB } = require('./mongodb-config');

class UserService {
  constructor() {
    this.db = null;
    this.collection = null;
  }

  async init() {
    this.db = await connectDB();
    this.collection = this.db.collection('users');
  }

  async createUser(userData) {
    try {
      const result = await this.collection.insertOne({
        ...userData,
        createdAt: new Date(),
        updatedAt: new Date()
      });
      return result.insertedId;
    } catch (error) {
      console.error('Error creating user:', error);
      throw error;
    }
  }

  async findUser(query) {
    try {
      return await this.collection.findOne(query);
    } catch (error) {
      console.error('Error finding user:', error);
      throw error;
    }
  }

  async findUsers(query = {}, options = {}) {
    try {
      const cursor = this.collection.find(query, options);
      return await cursor.toArray();
    } catch (error) {
      console.error('Error finding users:', error);
      throw error;
    }
  }

  async updateUser(filter, update) {
    try {
      const result = await this.collection.updateOne(
        filter,
        { $set: { ...update, updatedAt: new Date() } }
      );
      return result;
    } catch (error) {
      console.error('Error updating user:', error);
      throw error;
    }
  }

  async deleteUser(filter) {
    try {
      const result = await this.collection.deleteOne(filter);
      return result;
    } catch (error) {
      console.error('Error deleting user:', error);
      throw error;
    }
  }

  async getUsersByAge(minAge, maxAge) {
    try {
      return await this.collection.find({
        age: { $gte: minAge, $lte: maxAge }
      }).toArray();
    } catch (error) {
      console.error('Error finding users by age:', error);
      throw error;
    }
  }

  async getUsersBySkill(skill) {
    try {
      return await this.collection.find({
        skills: skill
      }).toArray();
    } catch (error) {
      console.error('Error finding users by skill:', error);
      throw error;
    }
  }
}

