// Redis operations in Node.js
const client = require('./redis-config');

class RedisService {
  // String operations
  async setString(key, value, expireSeconds = null) {
    try {
      if (expireSeconds) {
        await client.setex(key, expireSeconds, value);
      } else {
        await client.set(key, value);
      }
    } catch (error) {
      console.error('Error setting string:', error);
      throw error;
    }
  }

  async getString(key) {
    try {
      return await client.get(key);
    } catch (error) {
      console.error('Error getting string:', error);
      throw error;
    }
  }

  // Hash operations
  async setHash(key, fieldValue) {
    try {
      await client.hmset(key, fieldValue);
    } catch (error) {
      console.error('Error setting hash:', error);
      throw error;
    }
  }

  async getHash(key) {
    try {
      return await client.hgetall(key);
    } catch (error) {
      console.error('Error getting hash:', error);
      throw error;
    }
  }

  async getHashField(key, field) {
    try {
      return await client.hget(key, field);
    } catch (error) {
      console.error('Error getting hash field:', error);
      throw error;
    }
  }

  // List operations
  async pushToList(key, ...values) {
    try {
      return await client.lpush(key, ...values);
    } catch (error) {
      console.error('Error pushing to list:', error);
      throw error;
    }
  }

  async popFromList(key) {
    try {
      return await client.lpop(key);
    } catch (error) {
      console.error('Error popping from list:', error);
      throw error;
    }
  }

  async getList(key, start = 0, end = -1) {
    try {
      return await client.lrange(key, start, end);
    } catch (error) {
      console.error('Error getting list:', error);
      throw error;
    }
  }

  // Set operations
  async addToSet(key, ...members) {
    try {
      return await client.sadd(key, ...members);
    } catch (error) {
      console.error('Error adding to set:', error);
      throw error;
    }
  }

  async getSetMembers(key) {
    try {
      return await client.smembers(key);
    } catch (error) {
      console.error('Error getting set members:', error);
      throw error;
    }
  }

  async isSetMember(key, member) {
    try {
      return await client.sismember(key, member);
    } catch (error) {
      console.error('Error checking set membership:', error);
      throw error;
    }
  }

  // Sorted set operations
  async addToSortedSet(key, score, member) {
    try {
      return await client.zadd(key, score, member);
    } catch (error) {
      console.error('Error adding to sorted set:', error);
      throw error;
    }
  }

  async getSortedSetRange(key, start = 0, end = -1, withScores = false) {
    try {
      if (withScores) {
        return await client.zrange(key, start, end, 'WITHSCORES');
      }
      return await client.zrange(key, start, end);
    } catch (error) {
      console.error('Error getting sorted set range:', error);
      throw error;
    }
  }

  async getSortedSetRank(key, member) {
    try {
      return await client.zrank(key, member);
    } catch (error) {
      console.error('Error getting sorted set rank:', error);
      throw error;
    }
  }

  // Key operations
  async deleteKey(key) {
    try {
      return await client.del(key);
    } catch (error) {
      console.error('Error deleting key:', error);
      throw error;
    }
  }

  async keyExists(key) {
    try {
      return await client.exists(key);
    } catch (error) {
      console.error('Error checking key existence:', error);
      throw error;
    }
  }

  async setExpire(key, seconds) {
    try {
      return await client.expire(key, seconds);
    } catch (error) {
      console.error('Error setting expiration:', error);
      throw error;
    }
  }

  async getTTL(key) {
    try {
      return await client.ttl(key);
    } catch (error) {
      console.error('Error getting TTL:', error);
      throw error;
    }
  }
}

