// sqlite-config.js
const sqlite3 = require('sqlite3').verbose();
const { open } = require('sqlite');

async function openDB() {
  const db = await open({
    filename: './myapp.db',
    driver: sqlite3.Database
  });

  // Enable foreign keys
  await db.exec('PRAGMA foreign_keys = ON');

  return db;
}

module.exports = openDB;

// Task service with SQLite
const openDB = require('./sqlite-config');

class TaskService {
  constructor() {
    this.db = null;
  }

  async init() {
    this.db = await openDB();
    await this.createTables();
  }

  async createTables() {
    await this.db.exec(`
      CREATE TABLE IF NOT EXISTS users (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        name TEXT NOT NULL,
        email TEXT UNIQUE NOT NULL,
        age INTEGER,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP
      );

      CREATE TABLE IF NOT EXISTS tasks (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        title TEXT NOT NULL,
        description TEXT,
        completed BOOLEAN DEFAULT 0,
        user_id INTEGER,
        due_date DATE,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (user_id) REFERENCES users(id)
      );
    `);
  }

  async createTask(taskData) {
    const { title, description, user_id, due_date } = taskData;
    try {
      const result = await this.db.run(
        'INSERT INTO tasks (title, description, user_id, due_date) VALUES (?, ?, ?, ?)',
        [title, description, user_id, due_date]
      );
      return result.lastID;
    } catch (error) {
      console.error('Error creating task:', error);
      throw error;
    }
  }

  async getTaskById(id) {
    try {
      return await this.db.get(
        'SELECT * FROM tasks WHERE id = ?',
        [id]
      );
    } catch (error) {
      console.error('Error getting task:', error);
      throw error;
    }
  }

  async getTasksByUser(userId) {
    try {
      return await this.db.all(
        'SELECT * FROM tasks WHERE user_id = ? ORDER BY due_date',
        [userId]
      );
    } catch (error) {
      console.error('Error getting user tasks:', error);
      throw error;
    }
  }

  async getPendingTasks() {
    try {
      return await this.db.all(`
        SELECT 
          t.*,
          u.name as user_name
        FROM tasks t
        JOIN users u ON t.user_id = u.id
        WHERE t.completed = 0
        ORDER BY t.due_date
      `);
    } catch (error) {
      console.error('Error getting pending tasks:', error);
      throw error;
    }
  }

  async completeTask(id) {
    try {
      const result = await this.db.run(
        'UPDATE tasks SET completed = 1 WHERE id = ?',
        [id]
      );
      return result.changes > 0;
    } catch (error) {
      console.error('Error completing task:', error);
      throw error;
    }
  }

  async deleteTask(id) {
    try {
      const result = await this.db.run(
        'DELETE FROM tasks WHERE id = ?',
        [id]
      );
      return result.changes > 0;
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
    }
  }

  async getTaskStatistics() {
    try {
      const stats = await this.db.get(`
        SELECT 
          COUNT(*) as total_tasks,
          COUNT(CASE WHEN completed = 1 THEN 1 END) as completed_tasks,
          COUNT(CASE WHEN completed = 0 THEN 1 END) as pending_tasks,
          COUNT(CASE WHEN due_date < DATE('now') AND completed = 0 THEN 1 END) as overdue_tasks
        FROM tasks
      `);
      return stats;
    } catch (error) {
      console.error('Error getting task statistics:', error);
      throw error;
    }
  }

  async close() {
    if (this.db) {
      await this.db.close();
    }
  }
}

// Usage example
async function main() {
  const taskService = new TaskService();
  await taskService.init();

  try {
    // Create task
    const taskId = await taskService.createTask({
      title: 'Learn SQLite',
      description: 'Complete SQLite tutorial and practice examples',
      user_id: 1,
      due_date: '2024-01-25'
    });
    console.log('Task created with ID:', taskId);

    // Get pending tasks
    const pendingTasks = await taskService.getPendingTasks();
    console.log('Pending tasks:', pendingTasks);

    // Get statistics
    const stats = await taskService.getTaskStatistics();
    console.log('Task statistics:', stats);

    // Complete task
    await taskService.completeTask(taskId);
    console.log('Task completed');

  } catch (error) {
    console.error('Database operation failed:', error);
  } finally {
    await taskService.close();
  }
}

