const Database = require('better-sqlite3');

class FastTaskService {
  constructor() {
    this.db = new Database('./myapp.db');
    this.db.pragma('foreign_keys = ON');
    this.createTables();
  }

  createTables() {
    this.db.exec(`
      CREATE TABLE IF NOT EXISTS users (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        name TEXT NOT NULL,
        email TEXT UNIQUE NOT NULL,
        age INTEGER,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP
      );

      CREATE TABLE IF NOT EXISTS tasks (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        title TEXT NOT NULL,
        description TEXT,
        completed BOOLEAN DEFAULT 0,
        user_id INTEGER,
        due_date DATE,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (user_id) REFERENCES users(id)
      );
    `);
  }

  createTask(taskData) {
    const stmt = this.db.prepare(
      'INSERT INTO tasks (title, description, user_id, due_date) VALUES (?, ?, ?, ?)'
    );
    const result = stmt.run(taskData.title, taskData.description, taskData.user_id, taskData.due_date);
    return result.lastInsertRowid;
  }

  getAllTasks() {
    const stmt = this.db.prepare(`
      SELECT 
        t.*,
        u.name as user_name
      FROM tasks t
      JOIN users u ON t.user_id = u.id
      ORDER BY t.due_date
    `);
    return stmt.all();
  }

  close() {
    this.db.close();
  }
}
